%require-types
%new-style
%requires SqlUtil

bool sub data_ready() {
    DatasourcePool dsstage = sepl_get_dsp("gsi_staging");
    on_success dsstage.commit();
    on_error dsstage.rollback();

    # just an sql logging
    string sql;
    on_error log(LL_INFO, "sql: %s", sql);

    hash sh = (
            "columns" : ( cop_as(cop_count(), "cnt") ),
            "where"   : ("status"      : "N",),
        );

    SqlUtil::AbstractTable t = get_sql_table(dsstage, "it_master_data_item_dhl");
    return t.selectRow(sh, \sql).cnt > 0;
}

sub run() {
    sepl_create_master_order("IT-01-MASTER_DATA_ITEM-DHL-OUT", data_ready());
}
